import { Button, Table } from 'react-bootstrap';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
export default function Apiclass1() {
    const [data, setData] = useState([]);
    const [result, setResult] = useState("");

    useEffect(() => {
        
        getallusers();
    }, [])
    function getallusers() {
        setResult("Loading..");
        axios({
            method: "GET",
            url: "http://localhost/codeIgniter/ReactAPI/api/getAll",
        }).then(function (response) {
            // console.log(response);
            setData(response.data.users);
            setResult("");
        }).catch(function (response) {
            console.log(response);
        });
    }
    function deleteis(items) {
        axios({
            method: "GET",
            url: "http://localhost/codeIgniter/ReactAPI/api/deleteuser/" + items.id,
        }).then(function (response) {
            console.log(response);
            setResult(response.data.massage);
            setTimeout(() => {
                setResult("");
            }, 3000)
        }).catch(function (response) {
            console.log(response);
        });
    }
    return (
        <div>
            <h2>Api GET Method</h2>
            {result}
            <Table striped bordered hover className='maptbl' align='center'>
                <thead>
                    <tr>
                        <th>S.N</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>contact</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((item, index) =>
                            <tr key={index} id={index}>
                                <td>{index + 1}</td>
                                <td>{item.name}</td>
                                <td>{item.email}</td>
                                <td>{item.phone}</td>
                                <td><Button className='btn btn-danger btn-sm' onClick={() => deleteis(item)}>Delete</Button></td>
                            </tr>
                        )
                    }
                </tbody>
            </Table>
        </div>
    )
}
