import { Button, Table } from 'react-bootstrap';
import axios from 'axios';
import React, { useState, useEffect } from 'react';
export default function Apiclass1() {
    const [data, setData] = useState([]);
    const [result, setResult] = useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    useEffect(() => {
        getallusers();
    }, [])
    function getallusers() {
        // fetch("http://localhost/codeIgniter/ReactAPI/getAll1", {
        //     method: 'GET',
        //     // headers: { 'Accept': 'application/json', 'Content-Type': 'application/json' }
        // }).then((result) => {
        //     result.json().then((res) => {
        //         // console.log(res);
        //         setData(res.users);
        //     })
        // })
        setResult("Loading..");
        axios({
            method: "GET",
            url: "http://localhost/codeIgniter/ReactAPI/api/getAll",
        }).then(function (response) {
            // console.log(response);
            setData(response.data.users);
            setResult("");
        }).catch(function (response) {
            console.log(response);
        });
    }
    function editis(items) {
        setName(items.name)
        setEmail(items.email)
        setPhone(items.phone)
    }
    function deleteis(items) {
        axios({
            method: "GET",
            url: `http://localhost/codeIgniter/ReactAPI/api/deleteuser/${items.id}`,
        }).then(function (response) {
            // console.log(response);
            setResult(response.data.massage);
            getallusers();
            setTimeout(() => {
                setResult("");
            }, 3000)
        }).catch(function (response) {
            console.log(response);
        });
    }
    return (
        <div>
            <h2>Api GET Method</h2>
            {result}
            <div className='col-md-12'>
                <div className='row'>
                    <div className='col-md-8'>
                        <Table striped bordered hover className='maptbl' align='center' style={{ marginLeft: '10px' }}>
                            <thead>
                                <tr>
                                    <th>S.N</th>
                                    <th>Name</th>
                                    <th>Email</th>
                                    <th>contact</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    data.map((item, index) =>
                                        <tr key={index} id={index}>
                                            <td>{index + 1}</td>
                                            <td>{item.name}</td>
                                            <td>{item.email}</td>
                                            <td>{item.phone}</td>
                                            <td>
                                                <Button className='btn btn-danger btn-sm' onClick={() => deleteis(item)}>Delete</Button>
                                                <Button className='btn btn-warning btn-sm' onClick={() => editis(item)} style={{ marginLeft: '5px' }}>Edit</Button>
                                            </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </Table>
                    </div>
                    <div className='col-md-4'>
                        <form id="myform">
                            <input type="text" placeholder='Enter Name' onChange={(e) => setName(e.target.value)} /><br /><br />

                            <input type="email" placeholder='Email Id' onChange={(e) => setEmail(e.target.value)} /><br /><br />

                            <input type="number" placeholder='Enter Mobile No' onChange={(e) => setPhone(e.target.value)} /><br /><br />

                            <button type="submit" >Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}
