import React, { useState, Component } from 'react'
import './../App.css';
class Apiclass2 extends Component {
    constructor() {
        super();
        this.state = {
            name: "",
            email: "",
            phone: ""
        }
    }
    getFormData(e) {
        e.preventDefault()
        console.log({ 'name': this.state.name, 'email': this.state.email, 'phone': this.state.phone, 'photo': null });
        fetch("http://localhost/codeIgniter/ReactAPI/api/newuser", {
            method: 'POST',
            body: JSON.stringify({ 'name': this.state.name, 'email': this.state.email, 'phone': this.state.phone, 'photo': null })
        }).then((result) => {
            result.json().then((res) => {
                console.log(res);
                // this.setState({ name: "", email: "", phone: "" });
            })
        })
    }
    render() {
        return (
            <div className="User" >
                <h2>Api POST Method</h2>
                <form onSubmit={this.getFormData} id="myform">
                    <input type="text" value={this.state.name} placeholder='Enter Name' onChange={(e) => this.setState({ name: e.target.value })} /><br /><br />
                    <input type="email" value={this.state.email} placeholder='Email Id' onChange={(e) => this.setState({ email: e.target.value })} /><br /><br />

                    <input type="number" value={this.state.phone} placeholder='Enter Mobile No' onChange={(e) => this.setState({ phone: e.target.value })} /><br /><br />

                    <button type="submit" >Submit</button>
                </form>
            </div >
        )
    }
}
export default Apiclass2;
